mapper.select:
SELECT * FROM LOGIN_TB
WHERE 1=1

    AND ID = #{id}


    AND PHONENUMBER = #{phonenumber}


    AND PASSWD = #{password}


    AND EMAIL = #{email}

mapper.update:
UPDATE LOGIN_TB
SET firstname = #{firstname}, lastname = #{lastname}, id = #{id}, passwd = #{passwd}, email = #{email}, gender = #{gender}, birthday = #{birthday}, phonenumber = #{phonenumber}
WHERE id = #{id}
